INSERT INTO note (title,description,publish_date_time) VALUES
('Mazda озвучила дату презентации нового кроссовера CX-4', 'Японская компания Mazda озвучила дату презентации своего нового кроссовера CX-4, которая состоится на автосалоне в Пекине. Итак, официальная премьера внедорожника намечена на 25 апреля.', now()),
('В Украине стартуют продажи пяти новинок от АвтоВАЗ', 'Продажи сразу пяти новинок АвтоВАЗа стартуют на украинском рынке в ближайшее время. Следует отметить, что в Украину российская компания не повезет модели Lada Vesta и Lada Xray. О том, какие именно модификации авто поступят на украинский рынок не сообщается.', now()),
('Названы рублевые расценки на новейший кроссовер KIA Sportage', 'Одним из топовых корейских автомобильных брендов KIA были обнародованы данные, касающиеся рублевых расценок на новейшую генерацию кроссовера Sportage. Стоимость транспортного средства в Российской Федерации будет варьироваться от 1 900 000 до 2 200 000 рублей.', now()),
('Ford Focus нового поколения будет шире и длиннее', 'Судя по всему, четвертое поколение модели Focus будет не только длиннее, но и шире, если сравнивать с актуальной версией, присутствующей на рынке еще с 2010 года. Тестовый «мул» обладает удлиненным кузовом, взятым от третьего поколения Ford Focus.', now()),
('Volkswagen Polo 2017 получит новый дизайн и платформу', 'В преддверии выхода нового поколения модели Volkswagen Polo редакция издания OmniAuto подготовила рендерное изображение со своим видением дизайна будущей новинки. На данный момент известно, что хетчбэк будет базироваться на укороченном варианте платформы MQB.', now()),
('Renault Sandero лишился базовой версии', 'Компания Renault перестала оснащать российский хетчбэк Sandero базовым 1,2-литровым мотором мощностью 75 л.с. Теперь модель доступна лишь с 1,6-литровыми агрегатами на 82 и 102 л.с. Судя по всему, такое решение связано с непопулярностью маломощной модификации.', now()),
('Toyota обновила комплектации для кроссовера RAV4 в России', 'На российском авторынке в марте стал доступен к продаже японский кроссовер Toyota RAV4 в новой премиальной комплектации «Престиж Black». Новый автомобиль внутри теперь отделан кожей и премиальной замшей Black Sense.', now()),
('Renault покажет дизайн новых моделей на концептуальном спорткаре', 'Французский автомобильный бренд Renault покажет дизайн новых моделей на концептуальном спорткаре. Специалисты имеют большие планы на грядущий парижский автомобильный салон, который откроет свои двери для всех производителей транспортных средств уже этой осенью.', now()),
